import * as React from 'react';
import './index.css';

// Material
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { CardActionArea } from '@mui/material';

export default function App() {
  return (
    <div className="box">
      <NavBar/>

      <h1>Base UI + Create React App scaffold (JavaScript)</h1>
      <div className="item">
        <a href="https://mui.com/base-ui/">Base UI</a> is a library of unstyled React UI components
        and hooks.
      </div>
      <div className="item">
        <a href="https://create-react-app.dev/">Create React App</a> is a framework for quickly
        creating a new React project without the need to configure complex build tools or
        development environments.
      </div>
      <span>
        Created with ðŸ’™ by <a href="https://mui.com">MUI</a>.
      </span>
      <PageCard/>
    </div>
  );
}

const NavBar = () => {
  return (
    <Box className="navbar" sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>

          </Typography>
          <Button color="inherit">Home</Button>
          <Button color="inherit">About us</Button>
          <Button color="inherit">Register</Button>
        </Toolbar>
      </AppBar>
    </Box>
  );
}

const PageCard = () => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image="/static/images/cards/contemplative-reptile.jpg"
          alt="green iguana"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            Lizard
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Lizards are a widespread group of squamate reptiles, with over 6,000
            species, ranging across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  )
}
